        SET num_orders = FLOOR(RAND() * 5) + 1;

        SET j = 0;
        WHILE j < num_orders DO
            -- 插入 order 数据
            INSERT INTO orders (user_id, status, total_amount)
            VALUES (user_id, 'completed', RAND() * 1000);

